import { type User, type InsertUser, type Download, type InsertDownload, type AccessLog, type InsertAccessLog } from "@shared/schema";
import { randomUUID } from "crypto";

// modify the interface with any CRUD methods
// you might need

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;

  // Download tracking
  createDownload(download: InsertDownload): Promise<Download>;
  getDownloads(): Promise<Download[]>;
  getDownloadCount(): Promise<number>;

  // Access logs
  createAccessLog(log: InsertAccessLog): Promise<AccessLog>;
  getAccessLogs(limit?: number): Promise<AccessLog[]>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private downloads: Map<string, Download>;
  private accessLogs: Map<string, AccessLog>;

  constructor() {
    this.users = new Map();
    this.downloads = new Map();
    this.accessLogs = new Map();
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id };
    this.users.set(id, user);
    return user;
  }

  async createDownload(insertDownload: InsertDownload): Promise<Download> {
    const id = randomUUID();
    const download: Download = {
      id,
      timestamp: new Date(),
      userAgent: insertDownload.userAgent ?? null,
      ipAddress: insertDownload.ipAddress ?? null,
    };
    this.downloads.set(id, download);
    return download;
  }

  async getDownloads(): Promise<Download[]> {
    return Array.from(this.downloads.values()).sort(
      (a, b) => b.timestamp.getTime() - a.timestamp.getTime()
    );
  }

  async getDownloadCount(): Promise<number> {
    return this.downloads.size;
  }

  async createAccessLog(insertLog: InsertAccessLog): Promise<AccessLog> {
    const id = randomUUID();
    const log: AccessLog = {
      id,
      timestamp: new Date(),
      method: insertLog.method,
      path: insertLog.path,
      statusCode: insertLog.statusCode,
      duration: insertLog.duration,
      ipAddress: insertLog.ipAddress ?? null,
      userAgent: insertLog.userAgent ?? null,
    };
    this.accessLogs.set(id, log);
    return log;
  }

  async getAccessLogs(limit: number = 100): Promise<AccessLog[]> {
    const logs = Array.from(this.accessLogs.values()).sort(
      (a, b) => b.timestamp.getTime() - a.timestamp.getTime()
    );
    return logs.slice(0, limit);
  }
}

export const storage = new MemStorage();

