import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertDownloadSchema, insertAccessLogSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // Track a resume download
  app.post("/api/downloads", async (req, res) => {
    try {
      const downloadData = insertDownloadSchema.parse({
        userAgent: req.headers["user-agent"] || null,
        ipAddress: req.ip || req.headers["x-forwarded-for"] || null,
      });

      const download = await storage.createDownload(downloadData);
      res.json({ success: true, download });
    } catch (error) {
      res.status(400).json({ error: "Failed to track download" });
    }
  });

  // Get download statistics
  app.get("/api/downloads/stats", async (_req, res) => {
    try {
      const count = await storage.getDownloadCount();
      const downloads = await storage.getDownloads();

      res.json({
        totalDownloads: count,
        recentDownloads: downloads.slice(0, 10),
      });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch download stats" });
    }
  });

  // Get all downloads
  app.get("/api/downloads", async (_req, res) => {
    try {
      const downloads = await storage.getDownloads();
      res.json({ downloads });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch downloads" });
    }
  });

  // Get access logs
  app.get("/api/logs", async (req, res) => {
    try {
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 100;
      const logs = await storage.getAccessLogs(limit);
      res.json({ logs });
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch access logs" });
    }
  });

  return httpServer;
}

