client/src/pages/analytics.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Download, Activity, TrendingUp } from "lucide-react";
import type { Download as DownloadType, AccessLog } from "@shared/schema";
import { format } from "date-fns";

export default function Analytics() {
  const { data: statsData, isLoading: statsLoading } = useQuery<{
    totalDownloads: number;
    recentDownloads: DownloadType[];
  }>({
    queryKey: ["/api/downloads/stats"],
  });

  const { data: downloadsData, isLoading: downloadsLoading } = useQuery<{
    downloads: DownloadType[];
  }>({
    queryKey: ["/api/downloads"],
  });

  const { data: logsData, isLoading: logsLoading } = useQuery<{
    logs: AccessLog[];
  }>({
    queryKey: ["/api/logs"],
  });

  if (statsLoading || downloadsLoading || logsLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto" data-testid="loading-spinner"></div>
          <p className="mt-4 text-gray-600 dark:text-gray-400">Loading analytics...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-8">
      <div className="max-w-7xl mx-auto">
        <div className="mb-8">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-2" data-testid="text-analytics-title">
            Analytics Dashboard
          </h1>
          <p className="text-gray-600 dark:text-gray-400" data-testid="text-analytics-description">
            Monitor download activity and access logs
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Card data-testid="card-downloads-total">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Total Downloads</CardTitle>
              <Download className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-total-downloads">
                {statsData?.totalDownloads || 0}
              </div>
              <p className="text-xs text-muted-foreground">Resume downloads tracked</p>
            </CardContent>
          </Card>

          <Card data-testid="card-access-logs">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Access Logs</CardTitle>
              <Activity className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-total-logs">
                {logsData?.logs?.length || 0}
              </div>
              <p className="text-xs text-muted-foreground">Recent requests tracked</p>
            </CardContent>
          </Card>

          <Card data-testid="card-activity">
            <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
              <CardTitle className="text-sm font-medium">Recent Activity</CardTitle>
              <TrendingUp className="h-4 w-4 text-muted-foreground" />
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold" data-testid="text-recent-downloads">
                {statsData?.recentDownloads?.length || 0}
              </div>
              <p className="text-xs text-muted-foreground">Last 10 downloads</p>
            </CardContent>
          </Card>
        </div>

        <Tabs defaultValue="downloads" className="space-y-4">
          <TabsList>
            <TabsTrigger value="downloads" data-testid="tab-downloads">Downloads</TabsTrigger>
            <TabsTrigger value="logs" data-testid="tab-logs">Access Logs</TabsTrigger>
          </TabsList>

          <TabsContent value="downloads">
            <Card data-testid="card-downloads-table">
              <CardHeader>
                <CardTitle>All Downloads</CardTitle>
                <CardDescription>Complete history of resume downloads</CardDescription>
              </CardHeader>
              <CardContent>
                {downloadsData?.downloads && downloadsData.downloads.length > 0 ? (
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>Timestamp</TableHead>
                        <TableHead>User Agent</TableHead>
                        <TableHead>IP Address</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {downloadsData.downloads.map((download) => (
                        <TableRow key={download.id} data-testid={`row-download-${download.id}`}>
                          <TableCell data-testid={`text-download-time-${download.id}`}>
                            {format(new Date(download.timestamp), "PPpp")}
                          </TableCell>
                          <TableCell className="max-w-xs truncate" data-testid={`text-download-ua-${download.id}`}>
                            {download.userAgent || "Unknown"}
                          </TableCell>
                          <TableCell data-testid={`text-download-ip-${download.id}`}>
                            {download.ipAddress || "Unknown"}
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                ) : (
                  <p className="text-center text-gray-500 dark:text-gray-400 py-8" data-testid="text-no-downloads">
                    No downloads recorded yet
                  </p>
                )}
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="logs">
            <Card data-testid="card-logs-table">
              <CardHeader>
                <CardTitle>Access Logs</CardTitle>
                <CardDescription>Last 100 requests to the application</CardDescription>
              </CardHeader>
              <CardContent>
                {logsData?.logs && logsData.logs.length > 0 ? (
                  <Table>
                    <TableHeader>
                      <TableRow>
                        <TableHead>Timestamp</TableHead>
                        <TableHead>Method</TableHead>
                        <TableHead>Path</TableHead>
                        <TableHead>Status</TableHead>
                        <TableHead>Duration</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {logsData.logs.map((log) => (
                        <TableRow key={log.id} data-testid={`row-log-${log.id}`}>
                          <TableCell data-testid={`text-log-time-${log.id}`}>
                            {format(new Date(log.timestamp), "PPpp")}
                          </TableCell>
                          <TableCell data-testid={`text-log-method-${log.id}`}>
                            <span className={`font-mono text-sm px-2 py-1 rounded ${
                              log.method === 'GET' ? 'bg-blue-100 dark:bg-blue-900 text-blue-800 dark:text-blue-200' :
                              log.method === 'POST' ? 'bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200' :
                              'bg-gray-100 dark:bg-gray-800 text-gray-800 dark:text-gray-200'
                            }`}>
                              {log.method}
                            </span>
                          </TableCell>
                          <TableCell className="font-mono text-sm" data-testid={`text-log-path-${log.id}`}>
                            {log.path}
                          </TableCell>
                          <TableCell data-testid={`text-log-status-${log.id}`}>
                            <span className={`font-mono text-sm px-2 py-1 rounded ${
                              log.statusCode >= 200 && log.statusCode < 300 ? 'bg-green-100 dark:bg-green-900 text-green-800 dark:text-green-200' :
                              log.statusCode >= 400 ? 'bg-red-100 dark:bg-red-900 text-red-800 dark:text-red-200' :
                              'bg-gray-100 dark:bg-gray-800 text-gray-800 dark:text-gray-200'
                            }`}>
                              {log.statusCode}
                            </span>
                          </TableCell>
                          <TableCell className="font-mono text-sm" data-testid={`text-log-duration-${log.id}`}>
                            {log.duration}ms
                          </TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                ) : (
                  <p className="text-center text-gray-500 dark:text-gray-400 py-8" data-testid="text-no-logs">
                    No access logs recorded yet
                  </p>
                )}
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}
